}

.nav {
  display: flex;
  justify-content: space-between;
  color: white;
  position: fixed;
  width: 100%;
  align-items: center;
  padding: 2rem 6rem;
  font-family: $font-header;
  z-index: 20;
  transition: all 0.6s;

    @include media(600px){
      padding: 2rem 4rem;
    }


  &__logo {
    font-size: 3rem;
  }

  a {
    color: white;
    text-decoration: none;
    font-size: 1.6rem;
    overflow: hidden;

    @include media (800px){
      & {
        font-size: 1.4rem;
      }
    }
  }

  li {
    display: inline;
    padding: 0 3rem;
    font-family: $font-text;
  }

  &__actions {
    display: flex;
    align-items: center;


    .bi-search {
      font-size: 2rem;
      padding-right: 1rem;
    }

    a {
      position: relative;

      &::before {
        content: "";
        position: absolute;
        top: 0;
        left: 0;
        width: 100%;
        height: 100%;
        background: $background;
        transition: all .2s ease-in;
        z-index: -1;
        transform: translateX(-100%);
        border-radius: 2rem;
        font-size: 1.6rem;
        display: block;
      }
  
      &:hover::before {
        transform: translateX(0);
      }

      @include media (800px){
        display: none;
      }
    }
  }

  .menu-btn {
    display: none;
    font-size: 3rem;
  }

  @include media (1200px){
    .nav__links {
      display: none;
    }

    .menu-btn {
      display: block;
      font-size: 3rem;
      margin-left: 2rem;
    }
  }
}

.overlay {
  height: 0;
  overflow: hidden;
}

@include media (1200px){
  .overlay {
    position: fixed;
    width: 100vw;
    height: 0;
    background-color: black;
    z-index: 40 ;
    overflow: hidden;
    // pointer-events: none;

    .close-btn {
      font-size: 3rem;
      text-align: right;
      color: white;
      padding-right: 4rem;
      padding-top: 4rem;
    }

    .nav__links {
      font-family: $font-header;
      margin-top: 4rem;

      li {
        text-align: center;
        font-size: 3rem;
        margin-bottom: 2rem;
      }

      a {
        text-decoration: none;
        color: $orange;
      }
    }

    .btn--connect{
      display: inline-block;
      font-family: $font-header;
      font-size: 3rem;
      margin: 4rem;
    }
  }

  .overlay2 {
    background-color: $orange;
    z-index: 35;
  }
}

.navSticky {
  background-color: rgba(2, 8, 23, 0.923);
  padding: 2rem 4rem;

  @include media(1200px){
    &{
      padding: 2rem 3rem;
    }
  }
}

.hero {
