@use "../abstract/variables" as *;
@use "mixins" as *;

*,
*::after,
*::before {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
  /* border: 1px solid red; */
}

/* CSS RESET */

html, body, div, span, applet, object, iframe,
h1, h2, h3, h4, h5, h6, p, blockquote, pre,
a, abbr, acronym, address, big, cite, code,
del, dfn, em, img, ins, kbd, q, s, samp,
small, strike, strong, sub, sup, tt, var,
b, u, i, center,
dl, dt, dd, ol, ul, li,
fieldset, form, label, legend,
table, caption, tbody, tfoot, thead, tr, th, td,
article, aside, canvas, details, embed, 
figure, figcaption, footer, header, hgroup, 
menu, nav, output, ruby, section, summary,
time, mark, audio, video {
	margin: 0;
	padding: 0;
	font-size: 100%;
	font: inherit;
	vertical-align: baseline;
  box-sizing: border-box;
	/* border: 1px solid red; */
}

ol, ul {
	list-style: none;
}

html {
  font-size: 62.5%; 
  height: 100%;
  scroll-behavior: smooth;
}

body {
  background-color: rgb(2, 8, 23);
}

%btn {
  font-size: 1.4rem;
  border-radius: 5rem;
  padding: 1rem 2rem;
  text-decoration: none;
  font-family: $font-text;
  color: white;
}

.btn--cta {
  @extend %btn;
  background: $background;
}

.btn--border {
  @extend %btn;
  border: 1px solid white;

}

.py {
  padding-top: 12rem;
  padding-bottom: 12rem;
}

.px {
  padding-left: 12rem;
  padding-right: 12rem;
}

@include media (1200px){
  .py {
    padding-top: 12rem;
    padding-bottom: 12rem;
  }
  
  .px {
    padding-left: 8rem;
    padding-right: 8rem;
  }
}

@include media (800px){
  .py {
    padding-top: 8rem;
    padding-bottom: 8rem;
  }
  
  .px {
    padding-left: 4rem;
    padding-right: 4rem;
  }
}

// a {
//   font-size: 1.8rem;
//   background: rgb(251,76,59);
//   background: linear-gradient(90deg, rgba(251,76,59,1) 35%, rgba(226,43,109,1) 100%);  
// }

.heading {
  text-decoration: none;
  font-family: $font-text;
  margin-bottom: 2rem;

  &__sub {
    color: $orange;
    font-size: 2rem;
    text-decoration: underline;
    font-weight: bold;
    margin-bottom: 1rem;
    letter-spacing: 3px;
  }

  &__main {
    color: white;
    font-size: 5rem;
    font-weight: bold;
  }

  @include media (1200px){
    &__sub {
      font-size: 1.8rem;
    }

    &__main{
      font-size: 4rem;
    }
  }
}

