@use "../abstract/index" as *;

.nft-cards {
  display: grid;
  grid-template-columns: 1fr 1fr 1fr 1fr;
  grid-template-rows: 1fr;
  grid-gap: 4rem;
  justify-content: space-between;
}

.nft__card {
  background-color: $card-bg;
  padding: 1rem;
  font-family: $font-header;
  border-radius: .5rem;
  // width: 24%;

  &__image {
    text-decoration: none;
    overflow: hidden;
    
    img {
      width: 100%;
      border-radius: 1rem;
      transition: all 0.5s;

      &:hover {
        transform: scale(1.2); /* (150% zoom)*/
      }
    }
  }

  &__name {
    font-size: 1.8rem;
    color: white;
  }

  &__details {
    display: flex;
    justify-content: space-between;
    align-items: center;
  }


  .details__creator {
    display: flex;
    justify-content: space-between;
    margin: 2rem 0;
    align-items: center;

    &__image {
      width: 4rem;
      height: 4rem;
  
      img {
        width: 100%;
        border-radius: 50%;

       
      }
    }
  
    &__name {
      font-size: 1.6rem;
      color: white;
      padding-left: 1rem;
    }
  }

  .details__amount {
    display: flex;
    font-size: 1.8rem;
    color: white;

    .bi {
      color: green;
      padding-right: .5rem;
    }
  }


  .nft__cta {
    display: flex;
    justify-content: space-between;
    margin: 1.5rem 0;
  }

  .cta__history {
    display: flex;
    justify-content: space-between;
    font-size: 1.4rem;
    color: $white;
    align-items: center;

    .bi {
      color: $white;
      padding-right: 1rem;
    }
  }

  .cta__btn {
    font-size: 1.4rem;

    a {
      color: white;
      text-decoration: none;
      border: 1px solid white;
      padding: 1rem 1.5rem;
      border-radius: 2rem;
      position: relative;
      z-index: 5;
      display: block;
      overflow: hidden;
    }

    a::before {
      content: "";
      position: absolute;
      top: 0;
      left: 0;
      width: 100%;
      height: 100%;
      background: $background;
      transition: all .2s ease-in;
      z-index: -1;
      transform: translateX(-100%);
      border-radius: 2rem;
      font-size: 1.6rem;
    }

    a:hover::before {
      transform: translateX(0);
    }

    .bi {
      padding-right: 1rem;
    }
  }
}

// Explore NFt
.explore-cards {
  display: grid;
  grid-template-columns: 1fr 1fr 1fr 1fr;
  grid-template-rows: 1fr;
  grid-gap: 2rem;
}

@include media(1200px){
  .explore-cards, .nft-cards {
    grid-template-columns: 1fr 1fr;
    grid-template-rows: 1fr;
  }
}


@include media (600px){
  .explore-cards, .nft-cards {
    grid-template-columns: 1fr;
    grid-template-rows: 1fr;
    grid-gap: 2rem;
  }
}

